@import "tailwindcss";
@import "tw-animate-css";
@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@300;400;500;600;700&display=swap');

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
  --color-success: var(--success);
  --color-success-foreground: var(--success-foreground);
  --color-warning: var(--warning);
  --color-warning-foreground: var(--warning-foreground);

  --font-sans: 'Space Grotesk', system-ui, sans-serif;
}

:root {
  --radius: 0.75rem;

  /* Warm cream base with indigo accent */
  --background: hsl(40 20% 98%);
  --foreground: hsl(222 47% 11%);

  --card: hsl(0 0% 100%);
  --card-foreground: hsl(222 47% 11%);

  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(222 47% 11%);

  /* Deep indigo primary */
  --primary: hsl(234 89% 54%);
  --primary-foreground: hsl(0 0% 100%);
  --primary-glow: hsl(234 89% 70%);

  --secondary: hsl(220 14% 96%);
  --secondary-foreground: hsl(222 47% 11%);

  --muted: hsl(220 14% 96%);
  --muted-foreground: hsl(220 9% 46%);

  --accent: hsl(234 89% 97%);
  --accent-foreground: hsl(234 89% 44%);

  --destructive: hsl(0 84% 60%);

  --success: hsl(142 71% 45%);
  --success-foreground: hsl(0 0% 100%);

  --warning: hsl(38 92% 50%);
  --warning-foreground: hsl(0 0% 100%);

  --border: hsl(220 13% 91%);
  --input: hsl(220 13% 91%);
  --ring: hsl(234 89% 54%);

  --chart-1: hsl(234 89% 54%);
  --chart-2: hsl(142 71% 45%);
  --chart-3: hsl(38 92% 50%);
  --chart-4: hsl(280 65% 60%);
  --chart-5: hsl(0 84% 60%);

  --sidebar: hsl(0 0% 100%);
  --sidebar-foreground: hsl(222 47% 11%);
  --sidebar-primary: hsl(234 89% 54%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(234 89% 97%);
  --sidebar-accent-foreground: hsl(234 89% 44%);
  --sidebar-border: hsl(220 13% 91%);
  --sidebar-ring: hsl(234 89% 54%);

  /* Custom design tokens */
  --gradient-primary: linear-gradient(135deg, hsl(234 89% 54%), hsl(234 89% 70%));
  --gradient-hero: linear-gradient(135deg, hsl(234 89% 54%), hsl(280 65% 60%));
  --gradient-subtle: linear-gradient(180deg, hsl(40 20% 98%), hsl(220 14% 96%));

  --shadow-card: 0 1px 3px 0 hsl(222 47% 11% / 0.04), 0 1px 2px -1px hsl(222 47% 11% / 0.04);
  --shadow-elevated: 0 4px 6px -1px hsl(222 47% 11% / 0.05), 0 2px 4px -2px hsl(222 47% 11% / 0.05);
  --shadow-glow: 0 0 20px hsl(234 89% 54% / 0.15);
}

.dark {
  --background: hsl(222 47% 8%);
  --foreground: hsl(210 40% 98%);
  --card: hsl(222 47% 11%);
  --card-foreground: hsl(210 40% 98%);
  --popover: hsl(222 47% 11%);
  --popover-foreground: hsl(210 40% 98%);
  --primary: hsl(234 89% 64%);
  --primary-foreground: hsl(0 0% 100%);
  --secondary: hsl(222 47% 15%);
  --secondary-foreground: hsl(210 40% 98%);
  --muted: hsl(222 47% 15%);
  --muted-foreground: hsl(215 20% 65%);
  --accent: hsl(234 89% 20%);
  --accent-foreground: hsl(234 89% 74%);
  --destructive: hsl(0 84% 60%);
  --border: hsl(222 47% 18%);
  --input: hsl(222 47% 18%);
  --ring: hsl(234 89% 64%);
  --chart-1: hsl(234 89% 64%);
  --chart-2: hsl(142 71% 55%);
  --chart-3: hsl(38 92% 60%);
  --chart-4: hsl(280 65% 70%);
  --chart-5: hsl(0 84% 70%);
  --sidebar: hsl(222 47% 11%);
  --sidebar-foreground: hsl(210 40% 98%);
  --sidebar-primary: hsl(234 89% 64%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(234 89% 20%);
  --sidebar-accent-foreground: hsl(234 89% 74%);
  --sidebar-border: hsl(222 47% 18%);
  --sidebar-ring: hsl(234 89% 64%);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground font-sans antialiased;
  }
}

@layer components {
  .stat-card {
    @apply bg-card rounded-xl p-5 shadow-[var(--shadow-card)] border border-border transition-all duration-200;
  }

  .stat-card:hover {
    @apply shadow-[var(--shadow-elevated)];
  }

  .stat-card-hero {
    @apply relative overflow-hidden rounded-xl p-6 text-primary-foreground;
    background: var(--gradient-hero);
    box-shadow: var(--shadow-glow);
  }

  .stat-value {
    @apply text-3xl font-bold tracking-tight;
  }

  .stat-label {
    @apply text-sm font-medium text-muted-foreground;
  }

  .stat-label-light {
    @apply text-sm font-medium opacity-80;
  }

  .page-header {
    @apply text-3xl font-bold tracking-tight text-foreground;
  }

  .section-header {
    @apply text-lg font-semibold text-foreground;
  }

  .data-table {
    @apply w-full;
  }

  .data-table th {
    @apply text-left text-xs font-semibold uppercase tracking-wider text-muted-foreground px-4 py-3;
  }

  .data-table td {
    @apply px-4 py-3 text-sm;
  }

  .badge-success {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-success/10 text-success;
  }

  .badge-warning {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-warning/10 text-warning;
  }

  .badge-primary {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-primary/10 text-primary;
  }

  .input-field {
    @apply w-full rounded-lg border border-input bg-background px-3 py-2 text-sm transition-colors focus:border-primary focus:outline-none focus:ring-2 focus:ring-primary/20;
  }

  .dialog-overlay {
    @apply fixed inset-0 bg-foreground/20 backdrop-blur-sm;
  }
}

