                id="fachgebiet"
                value={formData.fachgebiet}
                onChange={(e) => setFormData({ ...formData, fachgebiet: e.target.value })}
              />
            </div>
            <DialogFooter>
              <Button type="button" variant="outline" onClick={() => setDialogOpen(false)}>
