        onOpenChange={setDeleteDialogOpen}
        title="Kurs löschen"
        description={`Möchten Sie den Kurs "${selectedKurs?.fields.titel}" wirklich löschen?`}
        onConfirm={handleDelete}
        loading={loading}
      />
    </div>
  );
}

