            </div>
            <div className="space-y-2">
              <Label htmlFor="kurs-beschreibung">Beschreibung</Label>
              <Textarea
                id="kurs-beschreibung"
                value={formData.beschreibung}
                onChange={(e) => setFormData({ ...formData, beschreibung: e.target.value })}
