                  onChange={(e) => setFormData({ ...formData, max_teilnehmer: parseInt(e.target.value) || 0 })}
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="kurs-preis">Preis (€) *</Label>
                <Input
                  id="kurs-preis"
